import React from "react";
import '../hourlyWeather.css';


var moment = require("moment");

const weather3Hours = (props) => {
  let newDate = new Date();
  const weekday = props.full.dt * 1000;
  newDate.setTime(weekday);

  const imgURL = `http://openweathermap.org/img/wn/${props.full.weather[0].icon}@2x.png`;

  return (
  <div>




<div className="container">
  <div className="info">
    <div className= "data-time">
      <h3 className="info-day">{moment(newDate).format('dddd')}</h3>
      <p className="text-muted">{moment(newDate).format('MMMM Do')}</p>
      <p className= "hour">{ moment(newDate).format('LT')}</p>
      </div>
      <p className="conditions"> {props.full.weather[0].description}</p>
      <img className="weatherIcon" src={imgURL} alt="weather icon"></img>
			<p className="temp"><span className="celicus">{Math.round(props.full.main.temp)}°c</span> |
      <span className="feh">{ Math.round((props.full.main.temp * 9) / 5 + 32)} °F</span></p>
      <p className="Humidity">Humidity: {props.full.main.humidity} %</p>
      <p className="windSpeed">wind: {props.full.wind.speed} kmh </p>
      </div>
    </div>
</div>
);
};

export default weather3Hours;
