import React, { Component } from "react";
import { Link } from "react-router-dom";

class SearchResults extends Component {
  state = { searchResults: "" };

  componentDidMount() {
    this.getResults();
  }

  componentDidUpdate() {
    this.getResults();
  }

  getResults = () => {
    this.setState({ searchResults: this.props.searchResults });
  };

  renderResults = (results) => {
    if (!results) console.log("results variable is undefined");
    else if (results.length === 0) return <p>No results</p>;
    else
      return (
        <ul
          style={{
            display: "flex",
            flexDirection: "column",
          }}
        >
          {results.slice(0, 10).map((result, index) => (
            <Link key={index} to={`/find/${result[2]}`}>
              {result[0]}, {result[1]}
            </Link>
          ))}
        </ul>
      );
  };

  render() {
    return (
      <div
        style={{
          border: "1px solid tomato",
          color: "tomato",
          margin: "20px",
        }}
      >
        <h1>SearchResults Component</h1>
        {this.renderResults(searchResults)}
      </div>
    );
  }
}

export default SearchResults;
