import React, { Component } from "react";
import config from "../config.json";
import WeatherDayCard from "./WeatherDayCard";

class Forecast extends Component {
  state = {
    city: null,
    country: null,
    latitude: null,
    longitude: null,
    temperature: null,
    feels_like: null,
    pressure: null,
    humidity: null,
    weatherDescription: null,
    weatherIcon: null,
    date: null,
    dailyData: [],
  };

  componentDidMount = () => {
    this.getForecast();
  };

  getForecast = () => {
    // if (this.props.cityId) {
    //   try {
    //     const weatherURL = `http://api.openweathermap.org/data/2.5/forecast?id=${this.props.cityId}&appid=${config.API_KEY}`;

    //     fetch(weatherURL)
    // .then((res) => res.json())
    // .then((data) => {
    const data = require(`../assets/forecast.json`);
    const dailyData = data.list.filter((reading) =>
      reading.dt_txt.includes("18:00:00")
    );
    this.setState(
      {
        dailyData: dailyData,
        city: data.city.name,
        country: data.city.country,
        temperature: data.list[0].main.temp,
        feels_like: data.list[0].main.feels_like,
        pressure: data.list[0].main.pressure,
        humidity: data.list[0].main.humidity,
        weatherDescription: data.list[0].weather[0].description,
        weatherIcon: data.list[0].weather[0].icon,
        date: data.list[0].dt_txt,
      },
      () => console.log(this.state)
    );
    // });
    // } catch (e) {
    //   console.log("Error in API request of Forecast component.", e);
    // }
    // }
  };

  weatherDaysCardsRender = () => {
    return this.state.dailyData.map((reading, index) => (
      <WeatherDayCard reading={reading} key={index} city={this.state.city} />
    ));
  };

  render() {
    return (
      <div className="forecast-container-hidden">
        <div id="go-back-arrow">
          <img
            src={process.env.PUBLIC_URL + "/img/back-arrow.png"}
            placeholder="img"
            alt="img"
          />
        </div>
        <div className="cards-container">{this.weatherDaysCardsRender()}</div>
      </div>
    );
  }
}

export default Forecast;
