import React from "react";
import "../WeatherDayCard.css";
var moment = require("moment");

const WeatherDayCard = ({ reading, celsius, minTemp, maxTemp }) => {
  let newDate = new Date();
  const weekday = reading.dt * 1000;
  newDate.setTime(weekday);
  let minTemperature = null;
  let maxTemperature = null;
  let unit = "";
  if (!celsius) {
    minTemperature = Math.round(((minTemp - 273.15) * 9) / 5 + 32);
    maxTemperature = Math.round(((maxTemp - 273.15) * 9) / 5 + 32);
    unit = " 째F";
  } else {
    minTemperature = Math.round(minTemp - 273.15);
    maxTemperature = Math.round(maxTemp - 273.15);
    unit = " 째C";
  }

  const imgURL = `http://openweathermap.org/img/wn/${reading.weather[0].icon}@2x.png`;

  return (
    <div className={reading.main.temp > 278 ? "card" : "card cold"}>
      <h3>{moment(newDate).format("dddd")}</h3>
      <p>{moment(newDate).format("MMMM Do")}</p>
      <img src={imgURL} alt="weather icon"></img>
      <h2>
        {minTemperature} / {maxTemperature}
        {unit}
      </h2>
      <div>
        <p id="forecast-description">{reading.weather[0].description}</p>
      </div>
    </div>
  );
};

export default WeatherDayCard;
