import React, { Component } from "react";
import config from "../config.json";
import Forecast from "./Forecast";

class Weather extends Component {
  state = {
    temperature: "",
    city: "",
    cityId: "",
    country: "",
    humidity: "",
    wind: "",
    description: "",
    sunrise: "",
    sunset: "",
    icon: "",
    error: "",
  };

  componentDidMount() {
    this.getWeather();
  }
  static getDerivedStateFromProps() {
    if (this.nextProps.cityId !== this.props.cityId) {
      this.setState({
        cityId: this.nextProps.cityId,
      });
    }
  }

  getWeather = async () => {
    try {
      let fetchUrl = "";
      if (!this.props.cityId) {
        fetchUrl = `http://api.openweathermap.org/data/2.5/weather?id=2673730&appid=${config.API_KEY}`;
        console.log("fetchUrl.using.stockholm:", fetchUrl);
      } else {
        fetchUrl = `http://api.openweathermap.org/data/2.5/weather?id=${this.props.cityId}&appid=${config.API_KEY}`;
        this.setState({ cityId: this.props.cityId });
        console.log("fetchUrl.from.props:", fetchUrl);
      }
      const api = await fetch(fetchUrl);
      const data = await api.json();
      if (data) {
        this.setState({
          city: data.name,
          country: data.sys.country,
          temperature: Math.round(data.main.temp - 273.15),
          humidity: data.main.humidity,
          wind: data.wind.speed,
          description: data.weather[0].description,
          sunrise: data.sys.sunrise,
          sunset: data.sys.sunset,
          icon: data.weather[0].icon,
          error: "",
        });
      }
    } catch (e) {
      console.log("Error in getWeather function of Weather component.", e);
    }
  };

  render() {
    const {
      sunrise,
      sunset,
      description,
      wind,
      humidity,
      temperature,
      country,
      city,
      icon,
      error,
    } = this.state;
    const { cityId } = this.props;
    const sunsriseClock = new Date(sunrise * 1000).toLocaleTimeString();
    const sunsetClock = new Date(sunset * 1000).toLocaleTimeString();
    const weatherCode = icon.slice(0, 2).toString();
    return (
      <div
        style={{
          border: "1px solid darkred",
          color: "darkred",
          margin: "20px",
        }}
      >
        <h1>Weather Component</h1>
        <h3>
          {city}, {country}
        </h3>

        <img
          src={process.env.PUBLIC_URL + "/img/" + weatherCode + ".png"}
          style={{ width: 100 }}
          placeholder="img"
          alt="img"
        />
        <ul>
          <li>
            Temperature : {temperature}°C / {(temperature * 9) / 5 + 32} °F
          </li>
          <li>Description : {description}</li>
          <li>Humidity : {humidity} %</li>
          <li>Wind : {wind} mph</li>
          <li>Sunrise : {sunsriseClock}</li>
          <li>Sunset : {sunsetClock}</li>
        </ul>
        {error && <p>error :{error}</p>}
        <Forecast cityId={cityId} />
      </div>
    );
  }
}

export default Weather;
