import React, { Component } from "react";
import "../hourlyWeather.css";
import moment from "moment";

class HourlyWeather extends Component {
  state = {
    fullData: [],
  };

  componentDidMount() {
    this.getForecast();
  }
  getForecast = () => {
    if (this.props.match.params.id) {
      // try {
      //   const weatherURL = `http://api.openweathermap.org/data/2.5/forecast?id=${this.props.cityId}&appid=${config.API_KEY}`;

      //   fetch(weatherURL)
      //     .then((res) => res.json())
      //     .then((data) => {
      const data = require(`../assets/forecast.json`);
      const dailyData = data.list.filter((reading) =>
        reading.dt_txt.includes("00:00:00")
      );
      const fullData = data["list"];
      this.setState(
        {
          fullData,
        },
        () => console.log("STATE", this.state)
      );
      // });
      // } catch (e) {
      //   console.log("Error in API request of Forecast component.", e);
      // }
    }
  };

  fullWeatherDetailRender = () => {
    return this.state.fullData.map((full, index) => (
      <HourlyWeather full={full} key={index} />
    ));
  };

  render() {
    const { fullData } = this.state;
    const imgURL = `http://openweathermap.org/img/wn/${fullData.weather[0].icon}@2x.png`;
    let newDate = new Date();
    const weekday = fullData.full.dt * 1000;
    newDate.setTime(weekday);

    return (
      <div>
        <div className="container">
          <div className="info">
            <div className="data-time">
              <h3 className="info-day">{moment(newDate).format("dddd")}</h3>
              <p className="text-muted">{moment(newDate).format("MMMM Do")}</p>
              <p className="hour">{moment(newDate).format("LT")}</p>
            </div>
            <p className="conditions">{fullData.weather[0].description}</p>
            <img className="weatherIcon" src={imgURL} alt="weather icon"></img>
            <p className="temp">
              <span className="celicus">
                {Math.round(fullData.main.temp)}°c
              </span>{" "}
              |
              <span className="feh">
                {Math.round((fullData.main.temp * 9) / 5 + 32)} °F
              </span>
            </p>
            <p className="Humidity">Humidity: {fullData.main.humidity} %</p>
            <p className="windSpeed">wind: {fullData.wind.speed} kmh </p>
          </div>
        </div>
      </div>
    );
  }
}

export default HourlyWeather;
