import React, { Component } from "react";
import Weather from "./Weather";

class Find extends Component {
  state = { cityId: this.props.match.params.id };
  componentDidMount() {
    this.renderComponent(this.state.cityId);
  }

  static getDerivedStateFromProps(nextProps, prevState) {
    if (nextProps.match.params.id !== prevState.cityId) {
      this.renderComponent(nextProps.match.params.id);
    }
  }

  renderComponent = (cityId) => {
    return (
      <div
        style={{ border: "1px solid green", color: "green", margin: "20px" }}
      >
        <h1>Find Component</h1>
        <Weather cityId={cityId} />
      </div>
    );
  };

  render() {
    return <div>{this.renderComponent()}</div>;
  }
}

export default Find;
