import React, { Component } from "react";
import "../hourlyWeather.css";
import moment from "moment";
import config from "../config.json";
import { getCountry } from "../services/citiesService";
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
} from "recharts";
import Forecast from "./Forecast";

class HourlyWeather extends Component {
  state = {
    fullData: [],
    city: "",
    country: "",
    celsius: true,
    fahrenheit: false,
    weatherChartsData: {},
  };

  componentDidMount() {
    if (this.props.match.params.celsius === "false") {
      this.setState({ celsius: false, fahrenheit: true });
    }
    this.getForecast();
  }
  getForecast = () => {
    if (this.props.match.params.id) {
      try {
        const weatherURL = `http://api.openweathermap.org/data/2.5/forecast?id=${this.props.match.params.id}&appid=${config.API_KEY}`;

        let forecast = new Forecast();

        fetch(weatherURL)
          .then((res) => res.json())
          .then((data) => {
            // const data = require(`../assets/forecast.json`);
            const {
              maxTemps,
              minTemps,
              descriptions,
              icons,
            } = forecast.dailyValues(data.list);
            const fullData = data["list"];
            const city = data["city"]["name"];
            const country = getCountry(data["city"]["country"]);
            this.setState(
              {
                fullData,
                city,
                country,
              },
              () => console.log("STATE", this.state)
            );
          });
      } catch (e) {
        console.log("Error in API request of Forecast component.", e);
      }
    }
  };

  weather3Hours = () => {
    const interval = this.state.fullData.map((full) => {
      let newDate = new Date();
      const weekday = full.dt * 1000;
      newDate.setTime(weekday);
      const icon = full.weather[0].icon.slice(0, 2).toString();
      const imgURL = process.env.PUBLIC_URL + "/img/" + icon + ".png";
      return this.fullWeatherDetailRender(
        newDate,
        full,
        imgURL,
        this.state.celsius
      );
    });
    return interval;
  };

  fullWeatherDetailRender = (newDate, full, imgURL, celsius, index) => {
    return (
      <div key={index} className="container">
        <div className="info">
          <div className="data-time">
            <h3 className="info-day">{moment(newDate).format("dddd")}</h3>
            <p className="text-muted">{moment(newDate).format("MMMM Do")}</p>
            <p className="hour">{moment(newDate).format("LT")}</p>
          </div>
          <p className="conditions">{full.weather[0].description}</p>
          <img className="weatherIcon" src={imgURL} alt="weather icon"></img>
          <p className="temp">
            <span>
              {celsius && Math.round(full.main.temp - 272.15)}
              {celsius && "°C"}
              {celsius || Math.round((full.main.temp * 9) / 5 - 459.67)}
              {celsius || "°F"}
            </span>
          </p>
          <p className="Humidity">Humidity: {full.main.humidity} %</p>
          <p className="windSpeed">Wind: {full.wind.speed} kmh </p>
        </div>
      </div>
    );
  };

  displayF = () => {
    this.setState({
      celsius: false,
      fahrenheit: true,
    });
  };

  displayC = () => {
    this.setState({
      celsius: true,
      fahrenheit: false,
    });
  };

  getTemps = () => {
    const temps = this.state.fullData.map((full) => {
      if (this.state.celsius) {
        console.log("temps1", Math.round(full.main.temp - 272.15));
        return Math.round(full.main.temp - 272.15);
      } else {
        return Math.round((full.main.temp * 9) / 5 - 459.67);
      }
    });

    return temps;
  };

  render() {
    const temps = this.getTemps();
    console.log("temps", temps);
    const weatherChartsData = [
      {
        name: moment(this.state.dateDay1).format("dddd"),
        temp: Math.round(this.state.tempDay1),
      },
      {
        name: moment(this.state.dateDay2).format("dddd"),
        temp: Math.round(this.state.tempDay2),
      },
      {
        name: moment(this.state.dateDay3).format("dddd"),
        temp: Math.round(this.state.tempDay3),
      },
      {
        name: moment(this.state.dateDay4).format("dddd"),
        temp: Math.round(this.state.tempDay4),
      },
      {
        name: moment(this.state.dateDay5).format("dddd"),
        temp: Math.round(this.state.tempDay5),
      },
    ];
    const { celsius, fahrenheit, city, country } = this.state;
    return (
      <main className="home-container">
        <div className="details-container">
          <h2>
            {city}
            <span>{country}</span>
          </h2>
          <div>
            <button onClick={this.displayC} disabled={celsius}>
              °C
            </button>
            <button onClick={this.displayF} disabled={fahrenheit}>
              °F
            </button>
          </div>
          <LineChart
            width={600}
            height={300}
            data={weatherChartsData}
            margin={{
              top: 5,
              right: 30,
              left: 20,
              bottom: 5,
            }}
          >
            <CartesianGrid strokeDasharray="3 3" />
            <XAxis dataKey="name" />
            <YAxis />
            <Tooltip />
            <Legend />
            <Line
              type="monotone"
              dataKey="temp"
              stroke="#8884d8"
              activeDot={{ r: 8 }}
            />
          </LineChart>
          <div className="weather-hours">{this.weather3Hours()}</div>
        </div>
      </main>
    );
  }
}

export default HourlyWeather;
