import React from "react";
import SearchResults from "./SearchResults";

const SearchBar = ({ value, onChange, results, resultsClass }) => {
  let displayedClass = resultsClass;
  function handleClick() {
    displayedClass = "results-container";
    console.log("clicked");
  }

  return (
    <React.Fragment>
      <form id="nav-search-bar">
        <input
          type="search"
          name="query"
          placeholder="Search"
          value={value}
          onChange={(e) => onChange(e.currentTarget.value)}
          onClick={handleClick}
        />
      </form>
      <SearchResults searchResults={results} resultsClass={displayedClass} />
    </React.Fragment>
  );
};

export default SearchBar;
