import React, { Component } from "react";
import "../hourlyWeather.css";
import moment from "moment";

class HourlyWeather extends Component {
  state = {
    fullData: [],
  };

  componentDidMount() {
    this.getForecast();
  }
  getForecast = () => {
    if (this.props.match.params.id) {
      // try {
      //   const weatherURL = `http://api.openweathermap.org/data/2.5/forecast?id=${this.props.cityId}&appid=${config.API_KEY}`;

      //   fetch(weatherURL)
      //     .then((res) => res.json())
      //     .then((data) => {
      const data = require(`../assets/forecast.json`);

      const fullData = data["list"];
      this.setState(
        {
          fullData,
        },
        () => console.log("STATE", this.state)
      );
      // });
      // } catch (e) {
      //   console.log("Error in API request of Forecast component.", e);
      // }
    }
  };

  weather3Hours = () => {
    this.state.fullData.forEach((full) => {
      let newDate = new Date();
      const weekday = full.dt * 1000;
      newDate.setTime(weekday);
      const imgURL = `http://openweathermap.org/img/wn/${full.weather[0].icon}@2x.png`;
      return this.fullWeatherDetailRender(newDate, full, imgURL);
    });
  };

  // return this.state.fullData.map((full, index) => (

  //   <Weather3Hours full={full} key={index}  />
  //   ));};

  fullWeatherDetailRender = (newDate, full, imgURL) => {
    return full.map((interval, index) => (
      <div key={index} className="container">
        <div className="info">
          <div className="data-time">
            <h3 className="info-day">{moment(newDate).format("dddd")}</h3>
            <p className="text-muted">{moment(newDate).format("MMMM Do")}</p>
            <p className="hour">{moment(newDate).format("LT")}</p>
          </div>
          <p className="conditions">{interval.weather[0].description}</p>
          <img className="weatherIcon" src={imgURL} alt="weather icon"></img>
          <p className="temp">
            <span className="celicus">{Math.round(interval.main.temp)}°c</span>{" "}
            |
            <span className="feh">
              {Math.round((interval.main.temp * 9) / 5 + 32)} °F
            </span>
          </p>
          <p className="Humidity">Humidity: {interval.main.humidity} %</p>
          <p className="windSpeed">wind: {interval.wind.speed} kmh </p>
        </div>
      </div>
    ));
  };

  render() {
    return <React.Fragment>{this.weather3Hours()}</React.Fragment>;
  }
}

export default HourlyWeather;
