import React, { Component } from "react";
import Weather from "./Weather";

class Find extends Component {
  state = { cityId: this.props.match.params.id };

  componentDidUpdate = (prevProps) => {
    console.log("COMPONENT DID UPDATE");
    if (this.props.match.params.id !== prevProps.match.params.id) {
      this.setState({ cityId: this.props.match.params.id });
      console.log("PARAMS ARE NOT THE SAME");
    }
  };

  render() {
    return (
      <div
        style={{ border: "1px solid green", color: "green", margin: "20px" }}
      >
        <h1>Find Component</h1>
        <Weather cityId={this.state.cityId} />
      </div>
    );
  }
}

export default Find;
