import React, { Component } from "react";
import "../hourlyWeather.css";
import moment from "moment";

class HourlyWeather extends Component {
  render() {
    const imgURL = `http://openweathermap.org/img/wn/${this.props.full.weather[0].icon}@2x.png`;
    let newDate = new Date();
    const weekday = this.props.full.dt * 1000;
    newDate.setTime(weekday);

    return (
      <div>
        <div className="container">
          <div className="info">
            <div className="data-time">
              <h3 className="info-day">{moment(newDate).format("dddd")}</h3>
              <p className="text-muted">{moment(newDate).format("MMMM Do")}</p>
              <p className="hour">{moment(newDate).format("LT")}</p>
            </div>
            <p className="conditions">
              {" "}
              {this.props.full.weather[0].description}
            </p>
            <img className="weatherIcon" src={imgURL} alt="weather icon"></img>
            <p className="temp">
              <span className="celicus">
                {Math.round(this.props.full.main.temp)}°c
              </span>{" "}
              |
              <span className="feh">
                {Math.round((this.props.full.main.temp * 9) / 5 + 32)} °F
              </span>
            </p>
            <p className="Humidity">
              Humidity: {this.props.full.main.humidity} %
            </p>
            <p className="windSpeed">wind: {this.props.full.wind.speed} kmh </p>
          </div>
        </div>
      </div>
    );
  }
}

export default HourlyWeather;
