import React, { Component } from "react";

class Weather extends Component {
  state = {
    temperature: "",
    humidity: "",
    wind: "",
    description: "",
    sunrise: "",
    sunset: "",
    icon: "",
    error: "",
  };

  componentDidMount() {
    this.getWeather();
  }

  getWeather = async () => {
    const api = await fetch(
      `http://api.openweathermap.org/data/2.5/weather?id=${this.props.city}&appid=4e934866ee0119b2698276839717ae38`
    );
    const data = await api.json();
    if (data) {
      this.setState({
        city: data.name,
        country: data.sys.country,
        temperature: Math.round(data.main.temp - 273.15),
        humidity: data.main.humidity,
        wind: data.wind.speed,
        description: data.weather[0].description,
        sunrise: data.sys.sunrise,
        sunset: data.sys.sunset,
        icon: data.weather[0].icon,
        error: "",
      });
    }
  };

  render() {
    const {
      sunrise,
      sunset,
      description,
      wind,
      humidity,
      temperature,
      country,
      city,
      icon,
      error,
    } = this.state;
    const sunsriseClock = new Date(sunrise * 1000).toLocaleTimeString();
    const sunsetClock = new Date(sunset * 1000).toLocaleTimeString();
    const weatherCode = icon.slice(0, 2).toString();
    console.log("ICON:", weatherCode);
    return (
      <React.Fragment>
        <div>
          <h1>
            {city}, {country}
          </h1>
        </div>
        <img
          src={process.env.PUBLIC_URL + "img/" + weatherCode + ".png"}
          placeholder="img"
          alt="img"
        />
        <ul>
          <li>
            Temperature :{" "}
            <span>
              {temperature}°C / {(temperature * 9) / 5 + 32} °F
            </span>
          </li>
          <li>
            Description : <span>{description}</span>
          </li>
          <li>
            Humidity : <span> {humidity} %</span>
          </li>
          <li>
            Wind : <span> {wind} mph</span>
          </li>
          <li>
            Sunrise : <span> {sunsriseClock}</span>
          </li>
          <li>
            Sunset : <span> {sunsetClock}</span>
          </li>
        </ul>
        {error && (
          <p>
            error :<span> {error}</span>
          </p>
        )}
      </React.Fragment>
    );
  }
}

export default Weather;
