import React, { Component } from "react";
import { Link } from "react-router-dom";
import SearchBar from "./SearchBar";
import { getCities, getCountry } from "../services/citiesService";

class NavBar extends Component {
  state = {
    searchQuery: [],
    searchResults: [],
    cities: [],
  };

  componentDidMount() {
    this.setState({ cities: getCities() });
  }

  handleSearch = (query) => {
    this.setState({ searchQuery: query });

    // GET CITIES
    const { cities: allCities } = this.state;

    let filtered = allCities;
    let results = [];
    let resultJoin = [];
    if (query) {
      let terms = query.split(" ");
      terms = terms.filter((term) => term !== "");
      console.log(terms);
      terms.map((term, index) => {
        if (index === 0) {
          results[index] = filtered.filter(
            (m) =>
              m.name.toLowerCase().includes(term.toLowerCase()) ||
              getCountry(m.country).toLowerCase().includes(term.toLowerCase())
          );
          results[index].map((result, index) => {
            resultJoin[index] = [
              result["name"],
              getCountry(result["country"]),
              result["id"],
            ].join(", ");
          });
        } else if (index > 0) {
          resultJoin = resultJoin.filter((m) =>
            m.toLowerCase().includes(term.toLowerCase())
          );
        }
      });
    }
    let searchResults = resultJoin.map((m) => m.split(", "));
    console.log("searchResults:", searchResults);
    this.setState({ searchResults });
  };

  render() {
    const { searchQuery, onSearch } = this.props;
    return (
      <header
        style={{ border: "1px solid purple", color: "purple", margin: "20px" }}
      >
        <h1>NavBar Component</h1>
        <h3>Weather App</h3>
        <nav style={{ display: "flex", flexDirection: "column" }}>
          <Link to="/">Home</Link>
          <Link to="/favourite-places">Favourite places</Link>
          <Link to="/help">Help</Link>
        </nav>
        <SearchBar
          value={this.state.searchQuery}
          onChange={this.handleSearch}
          results={this.state.searchResults}
        />
      </header>
    );
  }
}

export default NavBar;
